) { let window = window.into_inner(); let (camera, camera_transform) = camera.into_inner(); let mut debug_cursor = debug_cursor.into_inner(); if let Some(cursor_position) = window .cursor_position() .and_then(|cursor| camera.viewport_to_world_2d(camera_transform, cursor).ok()) { debug_cursor.0 = Some(cursor_position); } else { debug_cursor.0 = None; }
}

